# Create regions for each VOB\ncleartool mkregion -tag vA_region -vob vA\ncleartool mkregion -tag vB_region -vob vB\ncleartool mkregion -tag vC_region -vob vC\n\n# Define access control rules for each region\ncleartool mkacl -tag vA_region_acl -group gA -read -write\ncleartool mkaclelement -tag vA_region_acl -element * -group gA -read -write\n\ncleartool mkacl -tag vB_region_acl -group gB -read -write\ncleartool mkaclelement -tag vB_region_acl -element * -group gB -read -write\ncleartool mkaclelement -tag vB_region_acl -element * -group gA -read\n\ncleartool mkacl -tag vC_region_acl -group gC -read -write\ncleartool mkaclelement -tag vC_region_acl -element * -group gC -read -write