# Import the ACE OLE DB provider\nAdd-Type -AssemblyName "Microsoft.Office.Interop.Access.Dao"\n\n# Set the Excel file path and connection string\n$excelFilePath = "C:\Path\To\Your\Excel\File.xlsx"\n$connectionString = "Provider=Microsoft.ACE.OLEDB.12.0;Data Source=$excelFilePath;Extended Properties='Excel 12.0 Xml;HDR=YES'"\n\n# Create an OLE DB connection and command\n$conn = New-Object System.Data.OleDb.OleDbConnection $connectionString\n$cmd = New-Object System.Data.OleDb.OleDbCommand "SELECT * FROM [YourSheetName$]", $conn\n\n# Open the connection and execute the command\n$conn.Open()\n$dataReader = $cmd.ExecuteReader()\n\n# Extract the data into a DataTable\n$dataTable = New-Object System.Data.DataTable\n$dataTable.Load($dataReader)\n\n# Close the connection\n$conn.Close()