# Create an announcement list\n$listTitle = "Announcements"\n$listUrl = "https://your-site.sharepoint.com/Lists/Announcements"\nNew-SPOList -Title $listTitle -Url $listUrl -Template "Announcements"\n\n# Add an announcement web part to a page\n$pageUrl = "https://your-site.sharepoint.com/Pages/YourPage.aspx"\n$webPartZoneId = "Top"\n$webPartId = "Announcements"\n\nAdd-SPOWebPart -PageUrl $pageUrl -ZoneId $webPartZoneId -WebPartId $webPartId