Import-Module WebAdministration\n\n# Create a new request filtering rule\nNew-WebApplication -Name "MyApp" -Site "Default Web Site" -PhysicalPath "C:\inetpub\wwwroot" -Force\n\n# Configure request filtering\nSet-WebConfigurationProperty -Filter "/system.webServer/security/requestFiltering" -Name "allowHighBitCharacters" -Value $false -PSPath "IIS:\"\nSet-WebConfigurationProperty -Filter "/system.webServer/security/requestFiltering" -Name "allowDoubleEscaping" -Value $false -PSPath "IIS:\"