# Install the plugin\nInstall-SPFeature -Path "C:\Path\To\ Plugin\Feature.xml"\n\n# Configure the plugin\n$avSettings = Get-SPSecuritySettings -WebApplication "http://your-sharepoint-site"\n$avSettings.AntiVirusSettings.Enabled = $true\n$avSettings.AntiVirusSettings.ScanOnUpload = $true\n$avSettings.AntiVirusSettings.ScanOnDownload = $true\nSet-SPSecuritySettings -WebApplication "http://your-sharepoint-site" -SecuritySettings $avSettings