# Import the SQL Server PowerShell module\nImport-Module SqlServer\n\n# Set database connection details\n$dbServer = "your_db_server"\n$dbName = "your_db_name"\n\n# Set Source Safe repository details\n$sourceSafePath = "C:\path\to\source\safe\repo"\n\n# Retrieve SQL objects from database\n$sqlObjects = Invoke-SqlCmd -ServerInstance $dbServer -Database $dbName -Query `\n"SELECT name, type FROM sys.objects WHERE type IN ('P', 'TR', 'V', 'FN')"\n\n# Loop through SQL objects and check if they exist in Source Safe\nforeach ($obj in $sqlObjects) {\n  $objName = $obj.name\n  $objType = $obj.type\n\n  # Construct the expected file path in Source Safe\n  $expectedPath = Join-Path -Path $sourceSafePath -ChildPath ($objType + "\" + $objName + ".sql")\n\n  # Check if the file exists in Source Safe\n  if (!(Test-Path $expectedPath)) {\n    Write-Host "Missing from source control: $objName ($objType)"\n  }\n}