$solutionPath = "C:\Users\david\Documents\VS.Projects\CeoTrunk"\n$configurationToRemove = "Release|x86"\n\nGet-ChildItem -Path $solutionPath -Filter *.csproj -Recurse | ForEach-Object {\n    $projectFile = $_.FullName\n    $content = Get-Content -Path $projectFile\n    if ($content -match $configurationToRemove) {\n        Write-Host "Found reference to $configurationToRemove in $projectFile"\n        # Remove the stale reference\n        $updatedContent = $content -replace $configurationToRemove, ""\n        Set-Content -Path $projectFile -Value $updatedContent\n    }\n}