awk '\n  NR==FNR {  # Read the modifier file\n    id[$1] = $2  # Store the id and corresponding change in an array\n    next\n  }\n  {  # Read the data file\n    for (i in id) {\n      if ($0 ~ i) {  # Search for the id in the current line\n        $0 = gensub("(.{" 650 "}).{" 20 "}", "\\1" id[i], 1)  # Perform the modification\n        break\n      }\n    }\n    print\n  }\n' modifier.file data.file > output.file