# Create a new resource group\naz group create --name myResourceGroup --location "West US"\n\n# Create a new virtual machine\naz vm create --resource-group myResourceGroup --name myVM --image "MicrosoftWindowsServer:WindowsServer:2019-Datacenter:latest" --admin-username myAdmin --admin-password myPassword\n\n# Configure the VM's network settings\naz network nic create --resource-group myResourceGroup --name myNIC --vnet-name myVNet --subnet mySubnet\naz vm nic add --resource-group myResourceGroup --vm-name myVM --nic myNIC\n\n# Open the necessary ports for your application\naz network nsg rule create --resource-group myResourceGroup --nsg-name myNSG --name myRule --protocol tcp --source-port-range "*" --destination-port-range "80" --access allow --priority 100