#!/bin/bash\n\n# Loop through all files in the directory\nfor file in *.txt; do\n  # Get the next file in the sequence\n  next_file=$(echo "$file" | sed 's/\([0-9]\+\).txt/\1/; s/$/ + 1/; s/^/bul_9_5_/; s/$/.txt/')\n  \n  # Check if the next file exists\n  if [ -f "$next_file" ]; then\n    # Use diff to find the common lines between the two files\n    common_lines=$(diff --unchanged-group-format="" --changed-group-format="" "$file" "$next_file")\n    \n    # Remove the common lines from the end of the current file\n    sed -i "/$common_lines/d" "$file"\n  fi\ndone