# Create the .project file\necho "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<projectDescription>\n    <name>MyProject</name>\n    <comment></comment>\n    <projects></projects>\n    <buildSpec></buildSpec>\n    <natures></natures>\n</projectDescription>\n" > /path/to/project/.project\n\n# Create the .classpath file\necho "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<classpath>\n    <classpathentry kind=\"src\" path=\"src\"/>\n    <classpathentry kind=\"con\" path=\"org.eclipse.jdt.launching.JRE_CONTAINER\"/>\n    <classpathentry kind=\"output\" path=\"bin\"/>\n</classpath>\n" > /path/to/project/.classpath\n\n# Create the .location file\necho "/path/to/project" > /path/to/workspace/.metadata/.plugins/org.eclipse.core.resources/.projects/MyProject/.locatio