# Create a physical volume (PV) on the 1 TB drive\npvcreate /dev/sdX\n\n# Create a volume group (VG) on the PV\nvgcreate vg_dev /dev/sdX\n\n# Create logical volumes (LVs) for each partition\nlvcreate -L 20G -n lv_root vg_dev  # /\nlvcreate -L 10G -n lv_usr vg_dev   # /usr\nlvcreate -L 5G -n lv_var vg_dev    # /var\nlvcreate -L 2G -n lv_swap vg_dev   # swap\nlvcreate -L 100G -n lv_home vg_dev  # /home\nlvcreate -l 100%FREE -n lv_opt vg_dev  # /opt (optional)\n\n# Format and mount the LVs\nmkfs.ext4 /dev/mapper/vg_dev-lv_root\nmkfs.ext4 /dev/mapper/vg_dev-lv_usr\nmkfs.ext4 /dev/mapper/vg_dev-lv_var\nmkswap /dev/mapper/vg_dev-lv_swap\nmkfs.ext4 /dev/mapper/vg_dev-lv_home\nmkfs.ext4 /dev/mapper/vg_dev-lv_opt (if created)\n\n# Add entries to /etc/fstab\necho "/dev/mapper/vg_dev-lv_root / ext4 defaults 1 1" >> /etc/fstab\necho "/dev/mapper/vg_dev-lv_usr /usr ext4 defaults 1 2" >> /etc/fstab\necho "/dev/mapper/vg_dev-lv_var /var ext4 defaults 1 2" >> /etc/fstab\necho "/dev/mapper/vg_dev-lv_swap swap swap defaults 0 0" >> /etc/fstab\necho "/dev/mapper/vg_dev-lv_home /home ext4 defaults 1 2" >> /etc/fstab\necho "/dev/mapper/vg_dev-lv_opt /opt ext4 defaults 1 2" >> /etc/fstab (if created)