#!/bin/bash\n\n# Define database credentials\nDB_HOST="your_host"\nDB_USER="your_user"\nDB_PASSWORD="your_password"\nDB_NAME="your_database"\n\n# Define email recipient and sender\nRECIPIENT_EMAIL="admin@example.com"\nSENDER_EMAIL="your_email@example.com"\n\n# Create backup directory\nmkdir -p /path/to/backups\n\n# Create backup file\nmysqldump -h $DB_HOST -u $DB_USER -p$DB_PASSWORD $DB_NAME > /path/to/backups/backup.sql\n\n# Zip backup file\nzip /path/to/backups/backup.zip /path/to/backups/backup.sql\n\n# Email backup file\necho "Please find the database backup attached." | mail -s "WordPress Database Backup" -a /path/to/backups/backup.zip $RECIPIENT_EMAIL -- -f $SENDER_EMAIL