#!/bin/bash\n\n# Initialize the Git repo on the server\nssh server.domain "git init --bare /home/ME.git"\n\n# Clone the repo on each client\ngit clone ssh://server.domain/home/ME.git /home/ME\n\n# Add a cron job to push changes from each client to the server\ncrontab -l | grep -v "git push" ; echo "*/5 * * * * git push origin master"\n\n# Add a cron job to pull changes from the server to each client\ncrontab -l | grep -v "git pull" ; echo "*/5 * * * * git pull origin master"