#!/bin/bash\n\nsync() {\n  # Use --delete to remove files on the server that don't exist on the client\n  rsync -azvR --exclude-from=/home/ME/.rsync_excludes --delete -e 'ssh -ax' $1 $2\n  # Use --delete to remove files on the client that don't exist on the server\n  rsync -azvR --exclude-from=/home/ME/.rsync_excludes --delete -e 'ssh -ax' $2 $1\n}\n\nsync /home/ME server.domain:/home/ME