# Convert X.509 to OpenPGP using GnuPG\ngpg --import < x509_key.der\ngpg --export --armor > pgp_key.asc\n\n# Convert OpenPGP to X.509 using GnuPG\ngpg --import < pgp_key.asc\ngpg --export --x509 > x509_key.der\n\n# Convert X.509 to OpenSSH using OpenSSL and ssh-keygen\nopenssl rsa -in x509_key.der -outform pem > pem_key.pem\nssh-keygen -m PEM -i -f pem_key.pem > openssh_key\n\n# Convert OpenSSH to X.509 using ssh-keygen and OpenSSL\nssh-keygen -m PEM -e -f openssh_key > pem_key.pem\nopenssl rsa -in pem_key.pem -outform der > x509_key.der