#!/bin/bash\n\n# Set the file path and the time threshold (3 minutes)\nFILE_PATH="/path/to/your/file"\nTIME_THRESHOLD=3\n\n# Use find to check if the file has been modified within the last $TIME_THRESHOLD minutes\nif find "$FILE_PATH" -mmin -$TIME_THRESHOLD | grep -q .; then\n  echo "File has been modified within the last $TIME_THRESHOLD minutes"\n  # cp was successful, continue the script\nelse\n  echo "File has not been modified within the last $TIME_THRESHOLD minutes"\n  # cp failed, exit the script\n  exit 1\nfi