#!/bin/bash\n\n# Initialize the test plan\nplan() {\n  TEST PLAN=$1\n}\n\n# Output a test result\nok() {\n  if $1; then\n    echo "ok $TEST_NUMBER - $2"\n  else\n    echo "not ok $TEST_NUMBER - $2"\n  fi\n  ((TEST_NUMBER++))\n}\n\n# Output a test result with a message\nis() {\n  if [ "$1" = "$2" ]; then\n    echo "ok $TEST_NUMBER - $3"\n  else\n    echo "not ok $TEST_NUMBER - $3"\n  fi\n  ((TEST_NUMBER++))\n}\n\n# Output the final test results\ndone_testing() {\n  echo "1..$TEST_NUMBER"\n}