# in bbtest-net\n...\nif [ "$STATUS" = "red" ]; then\n  # Don't change color on first failure\n  echo "Delayed status update: waiting for bbretest confirmation"\n  bbhost -p $HOST -s "delayed_red"\nelse\n  bbhost -p $HOST -s "$STATUS"\nfi\n...\n\n# in bbretest\n...\nif [ "$PREV_STATUS" = "delayed_red" ]; then\n  # Confirm the issue, update color to red\n  bbhost -p $HOST -s "red"\nfi\n...