# deployment script (e.g., deploy.sh)\n#!/bin/bash\n\n# Set environment variables\nif [ "$ENV" = "prod" ]; then\n  API_KEY="prod-api-key"\n  DB_HOST="prod-db-host"\nelse\n  API_KEY="dev-api-key"\n  DB_HOST="dev-db-host"\nfi\n\n# Modify config files\nsed -i "s/API_KEY/$API_KEY/" config.php\nsed -i "s/DB_HOST/$DB_HOST/" config.php\n\n# Run deployment tasks (e.g., minify JavaScript)\ngulp deploy\n\n# Upload files to live server\nrsync -avz ./ /path/to/live/server/