# Full backup\nmysqldump -u [username] -p[password] --single-transaction --quick --lock-tables=false [database] > full_backup.sql\n\n# Incremental backup (binary log)\nmysql -u [username] -p[password] -e "FLUSH LOGS"\nmysql -u [username] -p[password] -e "PURGE BINARY LOGS BEFORE DATE_SUB(CURRENT_DATE, INTERVAL 7 DAY)"\n\n# To restore, use the mysqlbinlog command\nmysqlbinlog --start-datetime="YYYY-MM-DD HH:MM:SS" --stop-datetime="YYYY-MM-DD HH:MM:SS" binary_log_file | mysql -u [username] -p[password]