#!/bin/bash\n\n# Set backup directory\nBACKUP_DIR="/path/to/backup/folder"\n\n# Set backup frequency (e.g., daily, weekly)\nBACKUP_FREQ="daily"\n\n# Set backup retention (e.g., 30 days)\nBACKUP_RETENTION=30\n\n# Backup command (e.g., rsync, tar)\nBACKUP_CMD="rsync -avz --delete --exclude=.git /path/to/source/folder/ $BACKUP_DIR/"\n\n# Run backup command\n$BACKUP_CMD\n\n# Remove old backups\nfind $BACKUP_DIR -type d -mtime +$BACKUP_RETENTION -exec rm -rf {} \;