echo -n "Enter a string here: "\nread -e STRING\n\n# Remove non-alphanumeric characters and convert to lowercase\nSTRING=$(echo "$STRING" | tr -cd '[:alnum:] ' | tr ' ' '_' | tr '[:upper:]' '[:lower:]')\n\n# Alternatively, you can use parameter expansion\nSTRING=${STRING//[^a-zA-Z0-9 ]/}\nSTRING=${STRING,,}  # convert to lowercase\nSTRING=${STRING// /_}\n\necho "Sanitized string: $STRING"