# get proxy settings from registry\nproxy_enable=$(reg query "HKCU\Software\Microsoft\Windows\CurrentVersion\Internet Settings" /v ProxyEnable)\nproxy_server=$(reg query "HKCU\Software\Microsoft\Windows\CurrentVersion\Internet Settings" /v ProxyServer)\n\nif [ "$proxy_enable" -eq 1 ]; then\n    proxy_url="http://$proxy_server"\n    wget -e http_proxy=$proxy_url http://example.com/file.txt\nelse\n    wget http://example.com/file.txt\nfi