#!/bin/bash\n\n# Set the output file\nOUTPUT="disk_usage.png"\n\n# Get disk usage data\nDU_DATA=$(du -h --max-depth=1 / | sort -h)\n\n# Create a gnuplot script\necho "set terminal png size 800,600\nset output '$OUTPUT'\nset title 'Disk Usage'\nset xlabel 'Directory'\nset ylabel 'Size (MB)'\nset xtics rotate by -45\nplot '-' with boxes fs solid 0.5" > gnuplot_script\n\n# Add data to the gnuplot script\necho "$DU_DATA" | while read line; do\n  size=$(echo "$line" | cut -d' ' -f1)\n  dir=$(echo "$line" | cut -d' ' -f2-)\n  echo "\"$dir\" $size"\ndone >> gnuplot_script\n\n# Run gnuplot\ngnuplot gnuplot_script\n\n# Remove temporary script\nrm gnuplot_script