#!/bin/bash\n\n# Set the root folder of your repositories\nREPOSITORIES_ROOT=/path/to/repositories\n\n# Create an HTML file to store the list of repositories\nHTML_FILE=repositories.html\n\n# Create the HTML header\necho "<html><body><h1>Subversion Repositories</h1><ul>" > $HTML_FILE\n\n# Loop through the repositories and list them as links\nfor REPO in "$REPOSITORIES_ROOT"/*; do\n  if [ -d "$REPO" ]; then\n    REPO_NAME=$(basename "$REPO")\n    echo "<li><a href='http://your-svn-server.com/$REPO_NAME'>$REPO_NAME</a></li>" >> $HTML_FILE\n  fi\ndone\n\n# Close the HTML tags\necho "</ul></body></html>" >> $HTML_FILE