#!/bin/bash\n\n# Set the database credentials\nDB_HOST="localhost"\nDB_USER="your_username"\nDB_PASSWORD="your_password"\nDB_NAME="your_database"\n\n# Get the tables in the correct order to avoid foreign key errors\nTABLES=$(mysql -h ${DB_HOST} -u ${DB_USER} -p${DB_PASSWORD} ${DB_NAME} -s -N -e "SET FOREIGN_KEY_CHECKS=0; SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = '${DB_NAME}' ORDER BY TABLE_NAME; SET FOREIGN_KEY_CHECKS=1;")\n\n# Truncate each table\nfor TABLE in $TABLES; do\n  mysql -h ${DB_HOST} -u ${DB_USER} -p${DB_PASSWORD} ${DB_NAME} -s -N -e "TRUNCATE TABLE ${TABLE};"\ndone