#!/bin/bash\n\ninput_dir="path/to/input/files"\nsolution_dir="path/to/solution/files"\ncommand="./your_program"\n\nfor file in "$input_dir"/*.in; do\n  solution_file="$solution_dir/${file##*/}.soln"\n  if [ -f "$solution_file" ]; then\n    output=$($command < "$file")\n    solution=$(cat "$solution_file")\n    if [ "$output" != "$solution" ]; then\n      echo "Test failed: ${file##*/}"\n    else\n      echo "Test passed: ${file##*/}"\n    fi\n  fi\ndone