# Create a Cassandra cluster with 3 nodes\ndocker run -d --name node1 -p 9042:9042 cassandra:latest\ndocker run -d --name node2 -p 9043:9042 cassandra:latest\ndocker run -d --name node3 -p 9044:9042 cassandra:latest\n\n# Create a keyspace and table\ncqlsh -u cassandra -p cassandra node1\nCREATE KEYSPACE mykeyspace WITH REPLICATION = {'class': 'SimpleStrategy', 'replication_factor': 3};\nUSE mykeyspace;\nCREATE TABLE mytable (id int PRIMARY KEY, name text);