#!/bin/bash\n\n# Check if Oracle database is running\nif ps -ef | grep ora_pmon; then\n  # Check if database is installed\n  sqlplus -s / as sysdba <<EOF\n    SELECT * FROM V$DATABASE;\n    EXIT;\nEOF\n  if [ $? -eq 0 ]; then\n    # Database is installed and running, proceed with installation\n    echo "Database is installed and running, proceeding with installation..."\n  else\n    # Database is not installed or running, exit with error\n    echo "Database is not installed or running, exiting with error..."\n    exit 1\n  fi\nelse\n  # Oracle database is not running, exit with error\n  echo "Oracle database is not running, exiting with error..."\n  exit 1\nfi