#!/bin/bash\n\n# Define remote server and path\nREMOTE_SERVER="user@yourserver.com"\nREMOTE_PATH="/path/to/your/webroot"\n\n# Local path to your project (assuming this script is in your project root)\nLOCAL_PATH="$(dirname "$(readlink -f "$0")")"\n\n# Use rsync to update remote files\nrsync -avz --delete \\n  --exclude=/config.local.php \\n  --exclude=/vendor/ \\n  --exclude=.git/ \\n  $LOCAL_PATH/ $REMOTE_SERVER:$REMOTE_PATH/\n\necho "Deployment completed."