#!/bin/bash\n\nclassify_dirs() {\n  for dir in "$@"; do\n    if [ ! -d "$dir" ]; then\n      echo "Error: $dir is not a directory"\n      continue\n    fi\n\n    stat_out=$(stat -f -L --printf=%T:%t:%d:%i:%m:%s:%S "$dir")\n    read -r fstype rest <<< "$stat_out"\n\n    if [ "$fstype" = "nfs" ]; then\n      echo "--> $dir is an NFS mount point"\n    else\n      echo "--> $dir is local"\n    fi\n  done\n}\n\nclassify_dirs localdir remotedir