#!/bin/bash\n\n# The input string to check\nINPUT_STRING=$1\n\n# Check if the string length is between 2 and 63 characters\nif [ ${#INPUT_STRING} -lt 2 ] || [ ${#INPUT_STRING} -gt 63 ]; then\n    echo "String must be between 2 and 63 characters long."\n    exit 1\nfi\n\n# Check if the string starts or ends with a hyphen\nif [[ $INPUT_STRING =~ ^- ]] || [[ $INPUT_STRING =~ -$ ]]; then\n    echo "String must not start or end with a hyphen."\n    exit 1\nfi\n\n# Use grep with a regex to check if the string only contains alphanumeric characters and hyphens\nif ! echo "$INPUT_STRING" | grep -qE '^[a-zA-Z0-9-]+$'; then\n    echo "String can only contain alphanumeric characters and hyphens."\n    exit 1\nfi\n\n# If all checks pass\necho "The string is valid."