#!/bin/bash\n\n# Set Liquibase properties\nLIQUIBASE_HOME=/path/to/liquibase\nLIQUIBASE_CHANGELOG_FILE=db.changelog.xml\nLIQUIBASE_URL=jdbc:mysql://localhost:3306/mydb\nLIQUIBASE_USERNAME=myuser\nLIQUIBASE_PASSWORD=mypassword\n\n# Run Liquibase migration\njava -jar ${LIQUIBASE_HOME}/liquibase.jar \\n  --changeLogFile=${LIQUIBASE_CHANGELOG_FILE} \\n  --url=${LIQUIBASE_URL} \\n  --username=${LIQUIBASE_USERNAME} \\n  --password=${LIQUIBASE_PASSWORD} \\n  migrate