#!/bin/bash\n\n# Define the databases and their credentials\nDB1_HOST=localhost\nDB1_USER=myuser\nDB1_PASSWORD=mypassword\nDB1_NAME=mydb\n\nDB2_HOST=remotehost\nDB2_USER=myuser\nDB2_PASSWORD=mypassword\nDB2_NAME=mydb\n\n# Define the query to select the rows to transfer\nQUERY="SELECT * FROM mytable WHERE updated_at > (NOW() - INTERVAL 1 DAY)"\n\n# Dump the data from DB1 using the query\nmysqldump -h $DB1_HOST -u $DB1_USER -p$DB1_PASSWORD $DB1_NAME -t --skip-add-drop-table --skip-create-options --where="$QUERY" > data.sql\n\n# Import the data into DB2\nmysql -h $DB2_HOST -u $DB2_USER -p$DB2_PASSWORD $DB2_NAME < data.sql