#!/bin/bash\n\n# Set the source and destination paths\nSRC_PATH="/Users/<username>/Library/Application Support/iPhone Simulator/User/Applications/"\nDST_PATH="/Users/<username>/Library/Application Support/iPhone Simulator/User/Applications/"\n\n# Set the app ID (GUID)\nAPP_ID="6A3B4567-8901-2345-6789-012345678901"\n\n# Copy the .sb file and directory\ncp -R "${SRC_PATH}/${APP_ID}.sb" "${DST_PATH}/"\ncp -R "${SRC_PATH}/${APP_ID}" "${DST_PATH}/"\n\necho "Copied app to destination computer"