# File: /etc/init/usb-drive-handler.conf\n\nstart on dbus signal com.ubuntu.Upstart0_6::dev-disk-by\nstop on dbus signal com.ubuntu.Upstart0_6::dev-disk-remove\n\nscript\n  # Get the USB drive device path from the event\n  DEV_PATH=$(echo "$UPSTART_EVENTS" | grep -oP '(?<=dev-disk-by\()[^)]+')\n\n  # Check if it's the specific USB drive you're interested in\n  if [ "$DEV_PATH" = "/dev/disk/by-id/your_usb_drive_id" ]; then\n    # Mount the drive and copy files\n    /path/to/your/script.sh "$DEV_PATH"\n  fi\nend script