# Get the number of contributors\ncurl -s https://api.github.com/repos/<service-repo>/contributors | jq '.[] | .login' | wc -l\n\n# Get the number of open issues\ncurl -s https://api.github.com/repos/<service-repo>/issues | jq '.[] | .state' | grep -c 'open'\n\n# Get the number of recent commits\ncurl -s https://api.github.com/repos/<service-repo>/commits | jq '.[] | .commit.author.date' | grep -c '<recent-date>'