#!/bin/bash\n\n# Set the input file and output file\nINPUT_FILE=$1\nOUTPUT_FILE=$2\n\n# Convert Word documents to plain text\nif [ "${INPUT_FILE: -4}" == ".doc" ]; then\n  antiword -t "$INPUT_FILE" > "$OUTPUT_FILE"\nelif [ "${INPUT_FILE: -5}" == ".docx" ]; then\n  docx2txt "$INPUT_FILE" > "$OUTPUT_FILE"\nfi\n\n# Convert PDFs to plain text\nif [ "${INPUT_FILE: -4}" == ".pdf" ]; then\n  pdftotext -layout "$INPUT_FILE" "$OUTPUT_FILE"\nfi