# Get a list of all local repositories\ncurl -u user:password 'http://localhost:8081/nexus/service/local/repositories'\n\n# Iterate through the list and backup each repository\nfor repo in $(curl -u user:password 'http://localhost:8081/nexus/service/local/repositories' | grep -o '<id>.*</id>' | sed 's/<id>//g; s/<\/id>//g'); do\n  curl -u user:password "http://localhost:8081/nexus/service/local/repositories/$repo/content/archiva-internal" > "$repo.tar.gz"\ndone