@echo off\n\n:: Remove all existing virtual directories and applications\ncscript adsutil.vbs DELETE_VDIR "Default Web Site"\n\n:: Read the input list from a flat file (e.g., "iis_config.txt")\nfor /f "tokens=1,2,3,4,5 delims=," %%a in (iis_config.txt) do (\n  :: Add a virtual directory\n  cscript adsutil.vbs CREATE_VDIR "Default Web Site" %%a %%b\n  \n  :: Set the application base path\n  cscript adsutil.vbs SET_APPPOOL_PROPERTY "Default Web Site/%%a" AppPoolId %%c\n  \n  :: Set the ASP.NET version to 2.x if needed\n  if %%d=="2.x" cscript adsutil.vbs SET_W3SVC_PROPERTY "Default Web Site/%%a" AspScriptEngineVersion 2\n  \n  :: Set the application pool\n  cscript adsutil.vbs SET_APPPOOL_PROPERTY "Default Web Site/%%a" AppPoolId %%e\n)