#!/bin/bash\n\n# Set variables\nDB_HOST="localhost"\nDB_USER="backup_user"\nDB_PASSWORD="password"\nDB_NAME="my_database"\nBACKUP_DIR="/var/backups/mysql"\n\n# Create full backup\nmysqldump -h ${DB_HOST} -u ${DB_USER} -p${DB_PASSWORD} ${DB_NAME} > ${BACKUP_DIR}/backup.sql\n\n# Get current binary log position\nCURRENT_LOG=$(mysql -h ${DB_HOST} -u ${DB_USER} -p${DB_PASSWORD} -se "SHOW MASTER STATUS;" | awk '{print $4}')\n\n# Apply binary logs\nmysqlbinlog --start-position=${CURRENT_LOG} --stop-position=${CURRENT_LOG} ${DB_HOST}:${DB_NAME} | mysql -h ${DB_HOST} -u ${DB_USER} -p${DB_PASSWORD} ${DB_NAME}