#!/bin/bash\n\n# Set the root directory\nROOT_DIR=/path/to/code\n\n# Recursively add subdirectories to the PATH\nwhile IFS= read -r -d '' dir; do\n  export PATH="$PATH:$dir"\ndone < <(find "$ROOT_DIR" -type d -print0)\n\n# Optional: Make the changes permanent by adding the following line to your shell configuration file (e.g., ~/.bashrc)\n# export PATH=$(find "$ROOT_DIR" -type d -printf "%p:")