using PdfSharp;\nusing PdfSharp.Drawing;\nusing PdfSharp.Pdf;\n\nclass PdfCreator\n{\n    public void CreatePdf(string filePath)\n    {\n        // Create a new PDF document\n        PdfDocument document = new PdfDocument();\n        PdfPage page = document.AddPage();\n\n        // Create a graphics object\n        XGraphics gfx = XGraphics.FromPdfPage(page);\n\n        // Draw a string\n        gfx.DrawString("Hello, world!", new XFont("Arial", 12), XBrushes.Black, new XPoint(10, 10));\n\n        // Draw an image\n        XImage image = XImage.FromFile("path_to_your_image.jpg");\n        gfx.DrawImage(image, new XPoint(10, 20));\n\n        // Save the document\n        document.Save(filePath);\n    }\n}