public enum SessionKey\n{\n    MyVar1,\n    MyVar2,\n    MyVar3,\n    MyVar4\n}\n\n// Create a extension method to safely get/set session values\npublic static class SessionExtensions\n{\n    public static void SetSessionValue(this HttpSession session, SessionKey key, object value)\n    {\n        session[key.ToString()] = value;\n    }\n\n    public static object GetSessionValue(this HttpSession session, SessionKey key)\n    {\n        return session[key.ToString()];\n    }\n}