using System;\nusing System.Runtime.InteropServices;\n\npublic class RegistryWatcher\n{\n    private const string REGISTRY_KEY = "HKEY_CURRENT_USER\\YourKey";\n\n    [DllImport("advapi32.dll", SetLastError = true)]\n    private static extern int RegNotifyChangeKeyValue(IntPtr hKey, bool bWatchSubtree, int dwNotifyFilter, IntPtr hEvent, bool fAsynchronous);\n\n    public RegistryWatcher()\n    {\n        IntPtr key = Registry.CurrentUser.OpenSubKey(REGISTRY_KEY).Handle;\n        IntPtr eventHandle = CreateEvent(IntPtr.Zero, false, false, null);\n        RegNotifyChangeKeyValue(key, true, 0x00000008, eventHandle, true);\n\n        // Wait for the event to be triggered\n        WaitForSingleObject(eventHandle, 0xFFFFFFFF);\n    }\n\n    [DllImport("kernel32.dll")]\n    private static extern IntPtr CreateEvent(IntPtr lpEventAttributes, bool bManualReset, bool bInitialState, string lpName);\n\n    [DllImport("kernel32.dll")]\n    private static extern int WaitForSingleObject(IntPtr hHandle, int dwMilliseconds);\n}