using System.Xml.XPath;\n\n// Load XML document\nXPathDocument doc = new XPathDocument("example.xml");\n\n// Create XPath navigator\nXPathNavigator navigator = doc.CreateNavigator();\n\n// Use regular expression in XPath expression\nXPathExpression expression = navigator.Compile(".//div[matches(@id, '^foo\\d+$')]");\nXPathNodeIterator iterator = navigator.Select(expression);\n\n// Iterate over results\nwhile (iterator.MoveNext())\n{\n    Console.WriteLine(iterator.Current.OuterXml);\n}