using NUnit.Framework;\n\npublic class ReportQualityTests\n{\n    [Test]\n    public void WordCount_IsWithinTolerance()\n    {\n        // Arrange\n        var report = GenerateReport();\n        var expectedWordCount = 100;\n        var tolerance = 0.1; // 10% tolerance\n\n        // Act\n        var actualWordCount = report.WordCount;\n\n        // Assert\n        Assert.IsTrue(actualWordCount >= expectedWordCount * (1 - tolerance) &&\n                      actualWordCount <= expectedWordCount * (1 + tolerance));\n    }\n}