using Oracle.DataAccess.Client;\n\n// Create a new Oracle connection\nOracleConnection connection = new OracleConnection("Data Source=your_database;User Id=your_username;Password=your_password;");\n\n// Create a new Oracle command\nOracleCommand command = new OracleCommand("get_employee_by_id", connection);\ncommand.CommandType = CommandType.StoredProcedure;\n\n// Add input parameter\ncommand.Parameters.Add(new OracleParameter("p_employee_id", OracleDbType.Int32, ParameterDirection.Input)).Value = 123;\n\n// Add output parameter\ncommand.Parameters.Add(new OracleParameter("p_result", OracleDbType.RefCursor, ParameterDirection.Output));\n\n// Execute the stored procedure\nconnection.Open();\ncommand.ExecuteNonQuery();\n\n// Fetch the result\nOracleDataReader reader = ((OracleRefCursor)command.Parameters["p_result"].Value).GetDataReader();\nwhile (reader.Read())\n{\n    Console.WriteLine(reader["employee_name"]);\n}