using System.Diagnostics;\n\npublic class DllVersionReader\n{\n    public static FileVersionInfo GetFileVersionInfo(string dllPath)\n    {\n        return FileVersionInfo.GetVersionInfo(dllPath);\n    }\n\n    public static void Main(string[] args)\n    {\n        string dllPath = @"C:\Path\To\Your<Dll.dll";\n        FileVersionInfo versionInfo = GetFileVersionInfo(dllPath);\n\n        Console.WriteLine("File Version: " + versionInfo.FileVersion);\n        Console.WriteLine("Product Version: " + versionInfo.ProductVersion);\n        Console.WriteLine("File Name: " + versionInfo.OriginalFilename);\n    }\n}